import './App.css';
import React, { useState, useEffect } from 'react';
import Amplify from 'aws-amplify';
import { API, Storage } from 'aws-amplify';
import { AmplifyAuthenticator, AmplifySignOut, AmplifySignIn, AmplifySignUp } from '@aws-amplify/ui-react';
import { AuthState, onAuthUIStateChange } from '@aws-amplify/ui-components';
import awsconfig from './aws-exports';

Amplify.configure(awsconfig);

const Solve = () => {
  const [authState, setAuthState] = useState();
  const [user, setUser] = useState();

  useEffect(() => {
    onAuthUIStateChange((nextAuthState, authData) => {
        setAuthState(nextAuthState);
        setUser(authData)
    });
  }, []);

  return authState === AuthState.SignedIn && user ? (
    <div className="App">
      <h1>hi</h1>



      <AmplifySignOut />
    </div>
  ) : (
    <AmplifyAuthenticator>
      <AmplifySignIn
        headerText="mmmm"
        slot="signin">
        <a href='./AskNonAuth'>비회원등록하기</a>
      </AmplifySignIn>
      <AmplifySignUp
        slot="sign-up"
        formFields={[
          { type: "email" },
          { type: "password" },
          { type: "username"},
          {
              type: "gender",
              label: "Gender",
              placeholder: "Enter your Gender(male/female)",
              required: true
          },
          {
              type: "birthdate",
              label: "Birthdate",
              placeholder: "Enter your Birthdate(yyyy/mm/dd)",
              required: true
          },
        ]}
      />
    </AmplifyAuthenticator>
  );
}

export default Solve;
